#include <stdio.h>

int main() {
    int x = 10;  // Basit atama
    x += 5;      // x = x + 5
    printf("x += 5 sonucu: %d\n", x);

    x *= 2;      // x = x * 2
    printf("x *= 2 sonucu: %d\n", x);

    x /= 3;      // x = x / 3
    printf("x /= 3 sonucu: %d\n", x);

    return 0;
}

#include <stdio.h>

int main() {
    int a = 20, b = 3;

    printf("a = %d, b = %d\n", a, b);
    printf("Toplama: %d + %d = %d\n", a, b, a + b);
    printf("Çıkarma: %d - %d = %d\n", a, b, a - b);
    printf("Çarpma: %d * %d = %d\n", a, b, a * b);
    printf("Bölme: %d / %d = %d\n", a, b, a / b);
    printf("Mod: %d %% %d = %d\n", a, b, a % b);

    return 0;
}

#include <stdio.h>

int main() {
    int tam_sayi = 15;
    float ondalik = 4.7;

    printf("Orijinal değerler:\n");
    printf("tam_sayi = %d, ondalik = %.1f\n", tam_sayi, ondalik);

    // Explicit casting
    float sonuc1 = (float)tam_sayi / 4;
    int sonuc2 = (int)ondalik;

    printf("\nDönüşümler:\n");
    printf("(float)%d / 4 = %.2f\n", tam_sayi, sonuc1);
    printf("(int)%.1f = %d\n", ondalik, sonuc2);

    return 0;
}

