#include <stdio.h>
#include <math.h>

int main() {
    double sayi = 16.0;
    double aci = 3.14159 / 4;  // 45 derece radyan

    printf("Sayı: %.1f\n", sayi);
    printf("Karekök: %.2f\n", sqrt(sayi));
    printf("Karesi: %.1f\n", pow(sayi, 2));
    printf("Küpü: %.1f\n", pow(sayi, 3));

    printf("\nAçı: %.4f radyan\n", aci);
    printf("sin(%.4f) = %.4f\n", aci, sin(aci));
    printf("cos(%.4f) = %.4f\n", aci, cos(aci));
    printf("tan(%.4f) = %.4f\n", aci, tan(aci));

    return 0;
}

