#include <stdio.h>
#define PI 3.14159

int main() {
    float yaricap, alan, cevre;

    printf("Dairenin yarıçapını girin: ");
    scanf("%f", &yaricap);

    alan = PI * yaricap * yaricap;
    cevre = 2 * PI * yaricap;

    printf("\nDaire bilgileri:\n");
    printf("Yarıçap: %.2f\n", yaricap);
    printf("Alan: %.2f\n", alan);
    printf("Çevre: %.2f\n", cevre);

    return 0;
}

