#include <stdio.h>

int main() {
    int x = 10, y = 3;

    printf("=== ARİTMETİK OPERATÖRLER ===\n");
    printf("%d + %d = %d\n", x, y, x + y);
    printf("%d - %d = %d\n", x, y, x - y);
    printf("%d * %d = %d\n", x, y, x * y);
    printf("%d / %d = %d\n", x, y, x / y);
    printf("%d %% %d = %d\n", x, y, x % y);

    printf("\n=== ATAMA OPERATÖRLERİ ===\n");
    x += 5; printf("x += 5: %d\n", x);
    x -= 3; printf("x -= 3: %d\n", x);
    x *= 2; printf("x *= 2: %d\n", x);

    return 0;
}

