#include <stdio.h>
#include <math.h>

int main() {
    double sayi = 25.0;
    double aci = 3.14159 / 6;  // 30 derece

    printf("=== MATEMATİK FONKSİYONLARI ===\n");
    printf("Sayı: %.1f\n", sayi);
    printf("Karekök: %.2f\n", sqrt(sayi));
    printf("2. kuvvet: %.1f\n", pow(sayi, 2));
    printf("e tabanında log: %.4f\n", log(sayi));
    printf("10 tabanında log: %.4f\n", log10(sayi));

    printf("\n=== TRİGONOMETRİ ===\n");
    printf("Açı: %.4f radyan\n", aci);
    printf("sin: %.4f\n", sin(aci));
    printf("cos: %.4f\n", cos(aci));
    printf("tan: %.4f\n", tan(aci));

    return 0;
}
