codeExamples.code:
#include <stdio.h>
int main() {
    for (int i = 0; i < 5; i++) {
        printf("i değeri: %d\n", i);
    }
    return 0;
}

#include <stdio.h>
int main() {
    for (int i = 10; i > 0; i--) {
        printf("Geri sayım: %d\n", i);
    }
    printf("Başla!\n");
    return 0;
}

#include <stdio.h>
int main() {
    for (int i = 0; i <= 20; i += 2) {
        printf("Çift sayı: %d\n", i);
    }
    return 0;
}

#include <stdio.h>
int main() {
    int n = 5;
    int faktoriyel = 1;
    for (int i = 1; i <= n; i++) {
        faktoriyel *= i;
        printf("%d! = %d\n", i, faktoriyel);
    }
    return 0;
}

exercises.solution:
#include <stdio.h>
int main() {
    int toplam = 0;
    for (int i = 1; i <= 100; i++) {
        toplam += i;
    }
    printf("Toplam: %d\n", toplam);
    return 0;
}

#include <stdio.h>
int main() {
    int sayi;
    printf("Çarpım tablosu için sayı girin: ");
    scanf("%d", &sayi);
    for (int i = 1; i <= 10; i++) {
        printf("%d x %d = %d\n", i, sayi, i * sayi);
    }
    return 0;
}

#include <stdio.h>
int main() {
    for (int i = 1; i <= 5; i++) {
        for (int j = 1; j <= i; j++) {
            printf("*");
        }
        printf("\n");
    }
    return 0;
}

