#include <stdio.h>
int main() {
    int sayilar[5];
    for(int i=0; i<5; i++) {
        sayilar[i] = i * 10;
    }
    for(int i=0; i<5; i++) {
        printf("sayilar[%d] = %d\n", i, sayilar[i]);
    }
    return 0;
}

#include <stdio.h>
int main() {
    int dizi[3] = {10, 20, 30};
    int *ptr = dizi;
    for(int i=0; i<3; i++) {
        printf("dizi[%d] = %d, *(ptr+%d) = %d\n", i, dizi[i], i, *(ptr+i));
    }
    return 0;
}

#include <stdio.h>
int main() {
    int matris[2][3] = {{1,2,3}, {4,5,6}};
    for(int i=0; i<2; i++) {
        for(int j=0; j<3; j++) {
            printf("matris[%d][%d] = %d ", i, j, matris[i][j]);
        }
        printf("\n");
    }
    return 0;
}
