#include <stdio.h>
#include <string.h>
int main() {
    char a[20] = "C", b[20] = " Programlama";
    strcat(a, b);
    printf("%s\n", a);
    printf("Uzunluk: %lu\n", strlen(a));
    return 0;
}

#include <stdio.h>
#include <ctype.h>
int main() {
    char c = 'A';
    if(isalpha(c)) printf("%c bir harftir.\n", c);
    if(isdigit(c)) printf("%c bir rakamdır.\n", c);
    return 0;
}

