#include <stdio.h>
int main() {
    int oklar[5];
    for(int i=0; i<5; i++) {
        oklar[i] = (i+1)*10;
    }
    for(int i=0; i<5; i++) {
        printf("Ok %d hasar: %d\n", i+1, oklar[i]);
    }
    return 0;
}

#include <stdio.h>
int main() {
    int oklar[3] = {15, 25, 35};
    int *palantir = oklar;
    for(int i=0; i<3; i++) {
        printf("Palantir Ok %d: %d\n", i+1, *(palantir+i));
    }
    return 0;
}

#include <stdio.h>
int main() {
    int kale[2][2] = {{100,200},{150,250}};
    for(int i=0; i<2; i++) {
        for(int j=0; j<2; j++) {
            printf("Kule[%d][%d] Güç: %d ", i, j, kale[i][j]);
        }
        printf("\n");
    }
    return 0;
}
