#include <stdio.h>
#include <string.h>
int main() {
    char yuzuk[40] = "One Ring", ek[20] = " to Rule Them All";
    strcat(yuzuk, ek);
    printf("%s\n", yuzuk);
    printf("Yazıt uzunluğu: %lu\n", strlen(yuzuk));
    return 0;
}

#include <stdio.h>
#include <ctype.h>
int main() {
    char c = '9';
    if(isalpha(c)) printf("%c harf büyüsü.\n", c);
    if(isdigit(c)) printf("%c rakam büyüsü.\n", c);
    return 0;
}

